import React, { useEffect, useState } from "react";
import CardMain from "Components/Cards/main";
import { Button } from "Components";
import { useTranslation } from "react-i18next";
import { useSelector } from "react-redux";
import { useDispatch } from "react-redux";

function MyAccount() {
  const dispatch = useDispatch();

  const user = useSelector((state) => state.user);

  const [disabled, setDisabled] = useState(true);
  const { t } = useTranslation();
  const stateUser = useSelector((state) => state.getUserById?.user);

  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [lastName, setLastName] = useState("");
  const [mobile, setMobile] = useState("");

  useEffect(() => {
    if (user) {
      getUser();
    }
  }, [user]);

  function getUser() {
    dispatch({
      type: "GET_USER_BY_ID",
      payload: user?.id,
    });
  }

  useEffect(() => {
    if (stateUser) {
      setName(stateUser?.firstName);
      setEmail(stateUser?.email);
      setLastName(stateUser?.lastName);
      setMobile(stateUser?.mobileNumber);
      // setUserName(stateUser?.username);
    }
  }, [stateUser]);

  function update() {
    if (!disabled) {
      var temp = {
        idNumber: user?.idNumber,
        firstName: name,
        lastName: lastName,
        email: email,
        mobileNumber: mobile,
      };
      console.log("temp", temp);
      dispatch({
        type: "UPDATE_USER_DATA",
        payload: temp,
      });
      setTimeout(() => getUser(), 500);
    }
    setDisabled(!disabled);
  }
  return (
    <div>
      <CardMain width="w-full md:mt-0 mt-4" heading={t("My Profile")}>
        <div className="flex flex-row justify-center ">
          {/* <div className="h-32 w-32 overflow-hidden rounded-full border border-primary text-center justify-center flex  flex-row items-center text-primary hover:bg-gray-100 duration-200 cursor-pointer">
            <img
              src={
                "data:image/jpeg;base64,"
              }
              className="h-full w-full "
            />
          </div> */}
        </div>

        <div className="flex flex-col md:flex-row md:space-x-20 rtl:space-x-reverse mt-5">
          <div className="md:w-1/2 w-full  space-y-5">
            <InputField
              disabled={disabled}
              heading={t("First Name")}
              value={name}
              onChange={(e) => setName(e)}
            />
            <InputField
              disabled={true}
              heading={t("Email")}
              value={email}
              onChange={(e) => setEmail(e)}
            />

            <InputFieldMobile
              disabled={disabled}
              type="number"
              heading={t("Mobile Number")}
              value={mobile}
              onChange={(e) => setMobile(e)}
            />
          </div>
          <div className="md:w-1/2 w-full  space-y-5">
            <InputField
              disabled={disabled}
              heading={t("Last Name")}
              value={lastName}
              onChange={(e) => setLastName(e)}
            />
            <InputField
              disabled={true}
              heading={t("Username")}
              value={stateUser?.idNumber}
              onChange={(e) => console.log("helo", e)}
            />

            <InputField
              disabled={true}
              heading={t("Role")}
              value={stateUser?.roles[0]?.name}
              onChange={(e) => console.log("helo", e)}
            />
          </div>
        </div>
        <div className="flex flex-row justify-end mt-10">
          <Button
            buttonValue={disabled ? t("Edit") : t("Update")}
            buttonStyle="px-20 py-2"
            onButtonClick={() => update()}
          />
        </div>
      </CardMain>
    </div>
  );
}
export default MyAccount;

function InputField({ heading, type, value, disabled, onChange }) {
  return (
    <div className="flex flex-col w-full ">
      <a className="text-sm text-gray-700">{heading}</a>
      <input
        onChange={(e) => onChange(e.target.value)}
        disabled={disabled}
        value={value}
        type={type}
        className={` rounded-md h-10 py-2 outline-none mt-2 w-full  px-3 ${
          disabled ? "bg-gray-100" : "white border bordr-gray-200"
        }`}
      />
    </div>
  );
}

function InputFieldMobile({
  heading,
  type,
  value,
  disabled,
  onChange,
  validate,
}) {
  return (
    <div className="flex flex-col w-full">
      <a className="text-sm text-gray-700">{heading}</a>
      <input
        onChange={(e) => {
          const mobileNumber = e.target.value;
          onChange(mobileNumber);
          validate(mobileNumber); // Call the validation function
        }}
        disabled={disabled}
        value={value}
        type={type}
        className={`rounded-md h-10 py-2 outline-none mt-2 w-full px-3 ${
          disabled ? "bg-gray-100" : "white border bordr-gray-200"
        }`}
      />
    </div>
  );
}
